INSERT INTO articles (title, content, author_id, category_id, publication_date, image_url) VALUES ('Cụ bà bị hươu húc gãy 14 xương sườn', 'Bà được đưa vào Bệnh viện Đa khoa khu vực Tây Bắc Nghệ An cấp cứu hai tuần trước trong tình trạng lơ mơ, mạch nhanh, tụt huyết áp, khó thở, đau ngực, bụng chướng và có nhiều vết bầm tím. Bác sĩ chẩn đoán bệnh nhân sốc đa chấn thương, gãy 14 xương sườn, sập mảng ức, sườn, tràn máu, tràn khí màng phổi, tổn thương cơ tim, tụ máu kết mạc...
Bệnh viện kích hoạt báo động đỏ cấp cứu. Bệnh nhân được đặt ống nội khí quản, thở máy, sử dụng nhiều thuốc vận mạch liều cao, truyền máu. Bác sĩ phẫu thuật cố định mảng sườn di động và dẫn lưu khoang màng phổi cho bệnh nhân.', '00000000-0000-0000-0000-000000000000', (SELECT category_id FROM article_categories WHERE category_name = 'Tim mạch'), '2025-01-06 06:48:22', 'https://i1-suckhoe.vnecdn.net/2025/04/23/kiem-tra-suc-khoe-1745390545-7228-1745390741.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=vvjZ9tjYjCDxQbMDGOlTNQ');
