INSERT INTO articles (title, content, author_id, category_id, publication_date, image_url) VALUES ('Đạn bi súng đồ chơi xuyên thủng mặt bé trai', 'Kết quả chụp cắt lớp vi tính cho thấy dị vật xuyên thủng phía sau xoang hàm trái, sát hốc mắt, xoang mũi và các mạch máu lớn vùng mặt, đồng thời gây gãy thành trước và sau xoang cũng như tụ máu trong lòng xoang. Các tổn thương này được đánh giá đặc biệt nghiêm trọng, đe dọa đến chức năng thị giác và tính mạng nếu không được can thiệp kịp thời.
Các bác sĩ đã phẫu thuật nội soi khẩn cấp để lấy dị vật, xử lý tổn thương xương, cầm máu và bảo toàn các cấu trúc quan trọng trên khuôn mặt bệnh nhi. Sau mổ, bé tỉnh táo, phục hồi tốt, không để lại biến chứng nguy hiểm và được xuất viện sau ba ngày điều trị, chăm sóc hậu phẫu.
Bác sĩ Đông cho biết nơi này thường xuyên tiếp nhận các bệnh nhi bị tai nạn do đồ chơi, dị vật. Ông nhấn mạnh, trẻ nhỏ chưa có khả năng nhận biết rủi ro tiềm ẩn từ những vật tưởng chừng vô hại như súng đồ chơi, viên bi, nam châm... Vì vậy, việc giám sát, hướng dẫn và tạo dựng không gian vui chơi an toàn cho trẻ là yếu tố quan trọng nhằm phòng ngừa những tai nạn đáng tiếc.', '00000000-0000-0000-0000-000000000000', (SELECT category_id FROM article_categories WHERE category_name = 'Nhi khoa'), '2025-04-23 08:41:48', 'https://i1-suckhoe.vnecdn.net/2025/04/22/hi-nh-a-nh-vie-n-bi-du-o-c-la-5526-3213-1745334286.jpg?w=680&h=0&q=100&dpr=1&fit=crop&s=PlQ_GzUolrrv4HaQnjftlw');
SET @last_article_id = LAST_INSERT_ID();
